import React, { useState, useEffect } from 'react';
import FormContainer from '../components/FormContainer';
import { useDispatch, useSelector } from 'react-redux';
import { Button, Form, Row, Col } from 'react-bootstrap';
import { createBike } from '../actions/bikeActions';

const CreateBike = () => {
  // brand, model, nSerie, year, location, postalcode
  // implement isRecovered
  const [brand, setBrand] = useState('');
  const [model, setModel] = useState('');
  const [nSerie, setnSerie] = useState('');
  const [year, setYear] = useState('');
  const [location, setLocation] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const submitHandler = (e) => {};

  return (
    <FormContainer>
      <h1>Add Bike</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="brand">
          <Form.Label>Marca</Form.Label>
          <Form.Control
            type="brand"
            placeholder="Marca"
            value={brand}
            onChange={(e) => setBrand(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="model">
          <Form.Label>Modelo</Form.Label>
          <Form.Control
            type="model"
            placeholder="Modelo"
            value={model}
            onChange={(e) => setModel(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="nSerie">
          <Form.Label>Número Série</Form.Label>
          <Form.Control
            type="nSerie"
            placeholder="Número Série"
            value={nSerie}
            onChange={(e) => setnSerie(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="year">
          <Form.Label>Ano</Form.Label>
          <Form.Control
            type="ano"
            placeholder="Marca"
            value={year}
            onChange={(e) => setBrand(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="brand">
          <Form.Label>Marca</Form.Label>
          <Form.Control
            type="brand"
            placeholder="Marca"
            value={location}
            onChange={(e) => setBrand(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="brand">
          <Form.Label>Marca</Form.Label>
          <Form.Control
            type="brand"
            placeholder="Marca"
            value={postalCode}
            onChange={(e) => setBrand(e.target.value)}
          ></Form.Control>
        </Form.Group>
      </Form>
    </FormContainer>
  );
};

export default CreateBike;
